export const initialState = {
  isLoggedIn: false,
  me: null,
  signupData: {},
  loginData: {},
}

export const loginAction = (data) => {
  return {
    type: 'LOG_IN_REQUEST',
    data,
  }
}

export const logoutAction = () => {
  return {
    type: 'LOG_OUT_REQUEST',
  }
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'LOG_IN':
      return {
          ...state,
          isLoggingIn: true,
      }
    case 'LOG_IN_SUCCESS':
      return {
          ...state,
          isLoggingIn: false,
          isLoggedIn: true,
          me: action.data,
      }
    case 'LOG_IN_FAILURE':
      return {
          ...state,
          isLoggedIn: false,
          me: action.data,
      }      
    case 'LOG_OUT':
      return {
          ...state,
          isLoggedIn: false,
          me: null,
      }
    case 'LOG_OUT_SUCCESS':
      return {
          ...state,
          isLoggedIn: false,
          me: null,
      }
    case 'LOG_OUT_FAILURE':
      return {
          ...state,
          isLoggedIn: false,
          me: null,
      }
    default:
      return state;
  }
}

export default reducer;
