import { all, fork, call, put, takeLatest, delay } from 'redux-saga/effects'
import axios from 'axios';


function* watchAddPost() {
  yield takeLatest('ADD_POST_REQUEST')
}

export default function* rootSaga() {
  yield all([
    fork(watchLogin), 
    fork(watchLogOut),
    fork(watchAddPost),
  ]);
}
