export const initialState = {
  mainPosts: [{
    id: 1,
    User: {
      id: 1,
      nickname: 'test',
    },
    content: '첫 번째 게시글 #해시태그 #냥냥이',
    Images: [{
      src: 'https://i.pinimg.com/564x/c3/a0/22/c3a0224bf4d1eb00dca790910ccc620d.jpg'
    }, {
      src: 'https://i.pinimg.com/564x/af/e6/d5/afe6d5b3467da0d01b6affa24559bb77.jpg'
    }, {
      src: 'https://i.pinimg.com/564x/cf/fa/83/cffa83ebe67a00b3dfead14ce35060e9.jpg'
    }],
    Comment: [{
      User: {
        nickname: 'test1',
      },
      content: '너무너무 귀여워요!',
    }, {
      User: {
        nickname: 'test2',
      },
      content: '저도 키우고 싶어요',
    }]
  }],
  imagePaths: [],
  addPostLoading: false,
  addPostDone: false,
  addPostError: null,
}

export const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
export const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const ADD_POST_FAILURE = 'ADD_POST_FAILURE';

export const ADD_COMMENT_REQUEST = 'ADD_COMMENT_REQUEST';
export const ADD_COMMENT_SUCCESS = 'ADD_COMMENT_SUCCESS';
export const ADD_COMMENT_FAILURE = 'ADD_COMMENT_FAILURE';

export const addPost = (data) => ({
  type: ADD_POST_REQUEST,
  data,
});

export const addComment = (data) => ({
  type: ADD_COMMENT_REQUEST,
  data,
});

const dummyPost = {
  id: 2,
  content: '테스트 글 입니다',
  User: {
    id: 1,
    nickname: 'hwan',
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST_REQUEST:
      return {
        ...state,
        addPostLoading: true,
        addPostDone: false,
        addPostError: null,
      }
    case ADD_POST_SUCCESS:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
        addPostLoading: false,
        addPostDone: true,
      }
    case ADD_POST_FAILURE:
      return {
        ...state,
        addPostLoading: false,
        addPostError: action.error,
      }
      case ADD_POST_REQUEST:
        return {
          ...state,
          addPostLoading: true,
          addPostDone: false,
          addPostError: null,
        }
      case ADD_POST_SUCCESS:
        return {
          ...state,
          mainPosts: [dummyPost, ...state.mainPosts],
          addPostLoading: false,
          addPostDone: true,
        }
      case ADD_POST_FAILURE:
        return {
          ...state,
          addPostLoading: false,
          addPostError: action.error,
        }
    default:
      return state;
  }
}

export default reducer;
