export const initialState = {
  isLoggingIn: false, // 로그인 시도 중
  isLoggedIn: false,
  isLoggingOut: false, // 로그아웃 시도 중
  me: null,
  signupData: {},
  loginData: {},
}

export const loginAction = (data) => {
  return {
    type: 'LOG_IN_REQUEST',
    data,
  }
}

export const logoutAction = () => {
  return {
    type: 'LOG_OUT_REQUEST',
  }
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'LOG_IN':
      return {
          ...state,
          isLoggingIn: true,
      }
    case 'LOG_IN_SUCCESS':
      return {
          ...state,
          isLoggingIn: false,
          isLoggedIn: true,
          me: action.data,
      }
    case 'LOG_IN_FAILURE':
      return {
          ...state,
          isLoggingIn: false,
          isLoggedIn: false,
      }      
    case 'LOG_OUT':
      return {
          ...state,
          isLoggingOut: true,
          me: null,
      }
    case 'LOG_OUT_SUCCESS':
      return {
          ...state,
          isLoggingOut: false,
          isLoggedIn: false,
          me: null,
      }
    case 'LOG_OUT_FAILURE':
      return {
          ...state,
          isLoggedIn: false,
          me: null,
      }
    default:
      return state;
  }
}

export default reducer;
