import { all, fork, take, call } from 'redux-saga/effects'

function logInAPI() {
  return axios.post('/api/login')
}

function* logIn() {
  yield call(logInAPI)
}

function* watchLogin() {
  yield take('LOG_IN', logIn)
}

function* watchLogOut() {
  yield take('LOG_OUT')
}

function* watchAddPost() {
  yield take('ADD_POST')
}

export default function* rootSaga() {
  yield all([
    fork(watchLogin), 
    fork(watchLogOut),
    fork(watchAddPost),
  ]);
}
