import { HYDRATE } from 'next-redux-wrapper';
import { combineReducer } from 'redux';
import user from './user';
import post from './post';

const initialState = {
  user: {
  },
  post: {
  }
};

// (이전 상태, 액션) => 다음 상태
const rootReducer = combineReducer({
  index: (state = {}, action) => {
    switch (action.type) {
      case HYDRATE:
        return { ...state, ...action.payload };
      default:
        return state;
    }
  },
  user,
  post,
});

export default rootReducer;
