import React from 'react';
import { Button, ButtonGroup, Card, Popover } from 'antd';
import PropTypes from 'prop-types';
import { RetweetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design';

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={ post.Images[0] && <PostImages images={post.Images} /> }
        actions={[
          <RetweetOutlined key="retweet" />,
          <HeartOutlined key="heart" />,
          <MessageOutlined key="comment" />,
          <Popover key="more" content={(
            <ButtonGroup>
                <Button>수정</Button>
                <Button type="danger">삭제</Button>
                <Button>신고</Button>
            </ButtonGroup>
          )}>
            <EllipsisOutlined />
          </Popover>
        ]}
      >
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
}

PostCard.PropTypes
export default PostCard;
